// App.js - Future-Tech MI K√ºldet√©s f≈ë komponens
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Dashboard from './components/Dashboard';
import ModelUnlock from './components/ModelUnlock';
import EventCards from './components/EventCards';
import DataAnalysis from './components/DataAnalysis';
import Presentation from './components/Presentation';
import './styles/App.css';

const App = () => {
  // J√°t√©k √°llapot kezel√©se
  const [gameState, setGameState] = useState({
    credits: 50, // Kezd≈ë Tanul√°si kreditek (TK)
    unlockedModels: [], // Feloldott modellek
    completedChallenges: [], // Teljes√≠tett kih√≠v√°sok
    currentPhase: 'start', // Jelenlegi f√°zis
    playerName: '',
    teamName: 'Mint√°zatdek√≥dol√≥k'
  });

  const [currentView, setCurrentView] = useState('dashboard');

  // LocalStorage ment√©s √©s bet√∂lt√©s
  useEffect(() => {
    const savedState = localStorage.getItem('futuretech-gamestate');
    if (savedState) {
      setGameState(JSON.parse(savedState));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('futuretech-gamestate', JSON.stringify(gameState));
  }, [gameState]);

  // Modell felold√°sa
  const unlockModel = (modelType, cost) => {
    if (gameState.credits >= cost && !gameState.unlockedModels.includes(modelType)) {
      setGameState(prev => ({
        ...prev,
        credits: prev.credits - cost,
        unlockedModels: [...prev.unlockedModels, modelType]
      }));
      return true;
    }
    return false;
  };

  // Kih√≠v√°s teljes√≠t√©se
  const completeChallenge = (challengeId, reward = 0) => {
    if (!gameState.completedChallenges.includes(challengeId)) {
      setGameState(prev => ({
        ...prev,
        completedChallenges: [...prev.completedChallenges, challengeId],
        credits: prev.credits + reward
      }));
    }
  };

  // J√°t√©k √∫jraind√≠t√°sa
  const resetGame = () => {
    setGameState({
      credits: 50,
      unlockedModels: [],
      completedChallenges: [],
      currentPhase: 'start',
      playerName: '',
      teamName: 'Mint√°zatdek√≥dol√≥k'
    });
    localStorage.removeItem('futuretech-gamestate');
  };

  return (
    <Router>
      <div className="app">
        <header className="app-header">
          <div className="header-content">
            <div className="logo">
              <h1>üè≠ Future-Tech MI K√ºldet√©s</h1>
              <p>Mint√°zatdek√≥dol√≥k Akad√©mia</p>
            </div>
            <div className="game-stats">
              <div className="credit-display">
                <span className="credit-icon">ü™ô</span>
                <span className="credit-amount">{gameState.credits} TK</span>
              </div>
              <div className="team-info">
                <span>üë• {gameState.teamName}</span>
              </div>
            </div>
          </div>
        </header>

        <nav className="app-nav">
          <button 
            className={`nav-btn ${currentView === 'dashboard' ? 'active' : ''}`}
            onClick={() => setCurrentView('dashboard')}
          >
            üè† Ir√°ny√≠t√≥pult
          </button>
          <button 
            className={`nav-btn ${currentView === 'dashboard' ? 'active' : ''}`}
            onClick={() => setCurrentView('models')}
          >
            üß† Modellek
          </button>
          <button 
            className={`nav-btn ${currentView === 'dashboard' ? 'active' : ''}`}
            onClick={() => setCurrentView('events')}
          >
            üéØ K√ºldet√©sek
          </button>
          <button 
            className={`nav-btn \${currentView === 'data' ? 'active' : ''}`}
            onClick={() => setCurrentView('data')}
          >
            üìä Adatelemz√©s
          </button>
          <button 
            className={`nav-btn \${currentView === 'presentation' ? 'active' : ''}`}
            onClick={() => setCurrentView('presentation')}
          >
            üé§ Prezent√°ci√≥
          </button>
        </nav>

        <main className="app-main">
          {currentView === 'dashboard' && (
            <Dashboard 
              gameState={gameState}
              setGameState={setGameState}
              resetGame={resetGame}
            />
          )}
          {currentView === 'models' && (
            <ModelUnlock 
              gameState={gameState}
              unlockModel={unlockModel}
            />
          )}
          {currentView === 'events' && (
            <EventCards 
              gameState={gameState}
              completeChallenge={completeChallenge}
            />
          )}
          {currentView === 'data' && (
            <DataAnalysis 
              gameState={gameState}
            />
          )}
          {currentView === 'presentation' && (
            <Presentation 
              gameState={gameState}
            />
          )}
        </main>

        <footer className="app-footer">
          <p>¬© 2025 Future-Tech Gy√°rt√≥sor | Gamifik√°lt MI Oktat√°s</p>
          <button className="reset-btn" onClick={resetGame}>
            üîÑ √öjraind√≠t√°s
          </button>
        </footer>
      </div>
    </Router>
  );
};

export default App;
