// Dashboard.js - F≈ëoldal komponens
import React, { useState } from 'react';
import { motion } from 'framer-motion';

const Dashboard = ({ gameState, setGameState, resetGame }) => {
  const [showWelcome, setShowWelcome] = useState(!gameState.playerName);

  const handleNameSubmit = (e) => {
    e.preventDefault();
    const name = e.target.playerName.value;
    if (name.trim()) {
      setGameState(prev => ({
        ...prev,
        playerName: name.trim(),
        currentPhase: 'training'
      }));
      setShowWelcome(false);
    }
  };

  const getPhaseDescription = () => {
    const { unlockedModels, completedChallenges } = gameState;

    if (unlockedModels.length === 0) {
      return {
        title: "K√ºldet√©s I. f√°zis: Felk√©sz√ºl√©s",
        description: "Oldd fel az els≈ë modellt a selejt-felismer√©shez!",
        status: "üéØ Akt√≠v k√ºldet√©s"
      };
    } else if (unlockedModels.length === 1) {
      return {
        title: "K√ºldet√©s II. f√°zis: Fejleszt√©s", 
        description: "Szerezd meg a regresszi√≥s modellt az el≈ërejelz√©sekhez!",
        status: "‚ö° Folyamatban"
      };
    } else if (completedChallenges.length < 2) {
      return {
        title: "K√ºldet√©s III. f√°zis: K√ºldet√©sek",
        description: "Teljes√≠tsd az esem√©nyk√°rty√°k kih√≠v√°sait!",
        status: "üöÄ Halad√≥ szint"
      };
    } else {
      return {
        title: "K√ºldet√©s IV. f√°zis: Prezent√°ci√≥",
        description: "K√©sz√≠tsd el a v√©gs≈ë megold√°si tervet!",
        status: "üèÜ Befejez√©s"
      };
    }
  };

  if (showWelcome) {
    return (
      <motion.div 
        className="welcome-screen"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
      >
        <div className="welcome-content">
          <h2>üéØ √údv√∂z√∂l a Future-Tech Gy√°r!</h2>
          <div className="mission-briefing">
            <p>
              Tisztelt <strong>"Mint√°zatdek√≥dol√≥"</strong> alakulat!
            </p>
            <p>
              A Future-Tech gy√°r MI-rendszere seg√≠ts√©gre szorul. Rengeteg adat √©rkezik 
              a szenzorokb√≥l, de a rendszer nem tudja √©rtelmezni ≈ëket. 
            </p>
            <p>
              <strong>A k√ºldet√©setek:</strong> Tan√≠tsatok be egy g√©pi tanul√°si modellt, 
              amely felismeri a hib√°s term√©keket √©s el≈ërejelzi a termel√©si kapacit√°st.
            </p>
          </div>

          <form onSubmit={handleNameSubmit} className="name-form">
            <label htmlFor="playerName">Add meg a neved, Mint√°zatdek√≥dol√≥:</label>
            <input 
              type="text" 
              id="playerName" 
              name="playerName" 
              placeholder="pl. Kov√°cs Alex"
              required 
            />
            <button type="submit">üöÄ K√ºldet√©s Ind√≠t√°sa</button>
          </form>

          <div className="starting-resources">
            <h3>üìã Kezd≈ë er≈ëforr√°sok:</h3>
            <div className="resource-item">
              <span>ü™ô 50 Tanul√°si Kredit (TK)</span>
            </div>
            <div className="resource-item">
              <span>üìä 20 c√≠mk√©zett term√©k adata</span>
            </div>
            <div className="resource-item">
              <span>üéØ 4 esem√©nyk√°rtya kih√≠v√°s</span>
            </div>
          </div>
        </div>
      </motion.div>
    );
  }

  const phaseInfo = getPhaseDescription();

  return (
    <motion.div 
      className="dashboard"
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.6 }}
    >
      <div className="dashboard-header">
        <h2>üè† K√ºldet√©s Ir√°ny√≠t√≥pult</h2>
        <p>√údv, <strong>{gameState.playerName}</strong>! üëã</p>
      </div>

      <div className="mission-status">
        <div className="status-card current-phase">
          <h3>{phaseInfo.title}</h3>
          <p>{phaseInfo.description}</p>
          <span className="status-badge">{phaseInfo.status}</span>
        </div>

        <div className="status-grid">
          <div className="stat-card">
            <div className="stat-icon">ü™ô</div>
            <div className="stat-content">
              <h4>Tanul√°si Kreditek</h4>
              <p className="stat-value">{gameState.credits} TK</p>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-icon">üß†</div>
            <div className="stat-content">
              <h4>Feloldott Modellek</h4>
